import React, { useState } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  ScrollView,
} from "react-native";
import { Picker } from "@react-native-picker/picker";

const CareerForm = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [status, setStatus] = useState("Web Designer");
  const [experience, setExperience] = useState("");
  const [details, setDetails] = useState("");

  const handleSubmit = () => {
    // Handle form submission
  };

  return (
    <ScrollView contentContainerStyle={{ padding: 20 }}>
      <TextInput
        style={tailwind("bg-white border border-gray-300 rounded p-2 mb-2")}
        placeholder="Name"
        value={name}
        onChangeText={setName}
      />
      <TextInput
        style={tailwind("bg-white border border-gray-300 rounded p-2 mb-2")}
        placeholder="Contact number"
        value={phone}
        onChangeText={setPhone}
        keyboardType="phone-pad"
        maxLength={10}
      />
      <TextInput
        style={tailwind("bg-white border border-gray-300 rounded p-2 mb-2")}
        placeholder="Email"
        value={email}
        onChangeText={setEmail}
        keyboardType="email-address"
      />
      <Picker
        selectedValue={status}
        style={tailwind("bg-white border border-gray-300 rounded p-2 mb-2")}
        onValueChange={(itemValue) => setStatus(itemValue)}
      >
        <Picker.Item label="Web Designer" value="Web Designer" />
        <Picker.Item label="Web Developer" value="Web Developer" />
        <Picker.Item label="Mobile App Designer" value="Mobile App Designer" />
        <Picker.Item
          label="Mobile App Developer"
          value="Mobile App Developer"
        />
        <Picker.Item label="Digital Marketer" value="Digital Marketer" />
      </Picker>
      <TextInput
        style={tailwind("bg-white border border-gray-300 rounded p-2 mb-2")}
        placeholder="Years of Experience"
        value={experience}
        onChangeText={setExperience}
        keyboardType="numeric"
      />
      <TextInput
        style={tailwind("bg-white border border-gray-300 rounded p-2 mb-2")}
        placeholder="Other Details"
        value={details}
        onChangeText={setDetails}
        multiline
      />
      <TouchableOpacity
        style={tailwind("bg-blue-500 text-white py-2 px-4 rounded")}
        onPress={handleSubmit}
      >
        <Text style={tailwind("text-center")}>Submit</Text>
      </TouchableOpacity>
    </ScrollView>
  );
};

export default CareerForm;
