import React, { useState } from "react";

function CareerForm() {
  const [formData, setFormData] = useState({
    name: "",
    phone: "",
    email: "",
    status: "Web Designer",
    experience: "",
    details: "",
    fileToUpload: null,
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: name === "fileToUpload" ? files[0] : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here, for example, send data to server or perform validation
    console.log(formData);
    // Reset form data after submission if needed
    setFormData({
      name: "",
      phone: "",
      email: "",
      status: "Web Designer",
      experience: "",
      details: "",
      fileToUpload: null,
    });
  };

  return (
    <form
      className="flex flex-col gap-4 max-w-md mx-auto"
      onSubmit={handleSubmit}
    >
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={formData.name}
        onChange={handleChange}
        className="career-form-txt"
        required
      />
      <input
        type="tel"
        id="phone"
        name="phone"
        pattern="[0-9]{10}"
        placeholder="Contact number"
        value={formData.phone}
        onChange={handleChange}
        maxLength="10"
        className="career-form-phone"
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        className="career-form-email"
        required
      />
      <div className="radio-class">
        <h2 className="name">Apply For Which Post?</h2>
        <label className="radio">
          <input
            type="radio"
            name="status"
            value="Web Designer"
            checked={formData.status === "Web Designer"}
            onChange={handleChange}
            className="radio-one"
          />
          <span className="checkmark"></span>
          Web Designer
        </label>
        {/* Repeat labels for other options */}
      </div>
      <input
        type="number"
        id="experience"
        name="experience"
        placeholder="Years of Experience"
        value={formData.experience}
        onChange={handleChange}
        className="career-form-experience"
        required
      />
      <textarea
        placeholder="Other Details"
        name="details"
        value={formData.details}
        onChange={handleChange}
        className="career-form-txtarea"
        required
      ></textarea>
      <div className="file">
        <h2 className="name">Upload Your Resume</h2>
        <input
          type="file"
          name="fileToUpload"
          accept=".doc,.docx,.pdf"
          onChange={handleChange}
          className="upload"
          required
        />
        <br />
        <br />
        <br />
      </div>
      <input type="submit" value="Submit" className="career-form-btn" />
    </form>
  );
}

export default CareerForm;
