/* eslint-disable react/jsx-filename-extension */
import React, { useState } from "react";

import Button from "../elements/Button";
import HireMeModal from "../../components/HireMeModal";

export default function Discuss() {
  const [showModal, setShowModal] = useState(false);
  function showHireMeModal() {
    if (!showModal) {
      document
        .getElementsByTagName("html")[0]
        .classList.add("overflow-y-hidden");
      setShowModal(true);
    } else {
      document
        .getElementsByTagName("html")[0]
        .classList.remove("overflow-y-hidden");
      setShowModal(false);
    }
  }
  return (
    <section className="flex  mx-auto justify-center">
      <div className="flex flex-col w-10/12 sm:w-11/12 xl:w-10/12 rounded-2xl bg-theme-purple text-gray-100 py-14 sm:px-12 sm:py-20 xl:px-16 shadow-2xl discuss">
        <div className="flex flex-col sm:flex-row mb-3 sm:mb-9 p-5 sm:p-0 sm:items-center">
          <h1 className="w-2/3 text-5xl sm:text-6xl leading-tight font-semibold">
            Have an awesome project in mind?
          </h1>
          {showModal ? (
            <HireMeModal
              onClose={showHireMeModal}
              onRequest={showHireMeModal}
            />
          ) : null}
          {showModal ? showHireMeModal : null}
          <Button className="flex bg-white bg-yellow-300 text-blue-800 text-xl lg:text-2xl xl:text-xl tracking-wider items-center justify-center w-56 lg:w-96 lg:h-24 xl:w-56 h-20 p-5 border-2 border-dark-theme-purple shadow-xl rounded-full mt-6 sm:mt-0 sm:ml-4 lg:ml-0 xl:ml-12 transform transition duration-600 hover:scale-105">
            Lets Discuss!
          </Button>
        </div>
        <p className="font-light p-5 sm:p-0 text-lg sm:text-xl lg:text-2xl xl:text-xl mb-1">
          Lets discuss it and make your dream software come true!
        </p>
      </div>
    </section>
  );
}
